import React from 'react';
import PropTypes from 'prop-types';
import statisticsStyles from './statistics.styled';

const Statistics = ({ title, stats }) => {
  return (
    <section className={statisticsStyles.statistics}>
      {title && <h2 className={statisticsStyles.title}>{title}</h2>}

      <ul className={statisticsStyles.statList}>
        {stats.map((item) => (
          <li key={item.id} className={statisticsStyles.item}>
            <span className={statisticsStyles.label}>{item.label}</span>
            <span className={statisticsStyles.percentage}>{item.percentage}%</span>
          </li>
        ))}
      </ul>
    </section>
  );
};

Statistics.propTypes = {
  title: PropTypes.string,
  stats: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      percentage: PropTypes.number.isRequired,
    })
  ).isRequired,
};

export default Statistics;
