import React from 'react';
import PropTypes from 'prop-types';
import { StatisticsSection, StatisticsTitle, StatList, StatItem, Label, Percentage } from './statistics.styled';

const Statistics = ({ title, stats }) => {
  return (
    <StatisticsSection className="statistics">
      {title && <StatisticsTitle className="title">{title}</StatisticsTitle>}
      <StatList className="stat-list">
        {stats.map(({ id, label, percentage }) => (
          <StatItem key={id} className="item">
            <Label>{label}</Label>
            <Percentage>{percentage}%</Percentage>
          </StatItem>
        ))}
      </StatList>
    </StatisticsSection>
  );
};

Statistics.propTypes = {
  title: PropTypes.string,
  stats: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      percentage: PropTypes.number.isRequired,
    })
  ).isRequired,
};

export default Statistics;
