const profileStyles = {
  profile: {
    display: 'flex',
    alignItems: 'center',
    border: '2px solid #ccc',
    padding: '20px',
    borderRadius: '5px',
  },
  description: {
    flex: 1,
    textAlign: 'center',
    marginRight: '20px',
  },
  avatar: {
    width: '150px',
    height: '150px',
    borderRadius: '50%',
  },
  name: {
    fontSize: '24px',
    fontWeight: 'bold',
  },
  tag: {
    fontSize: '16px',
    color: '#666',
  },
  location: {
    fontSize: '16px',
    color: '#999',
  },
  stats: {
    listStyle: 'none',
    margin: '0',
    padding: '0',
  },
  statItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '10px',
  },
  label: {
    flex: 1,
    fontSize: '16px',
  },
  quantity: {
    fontSize: '16px',
    fontWeight: 'bold',
  },
};

export default profileStyles;
