import React from 'react';
import Profile from './Profile';
import userData from './data/user.json'; // Припустимо, що ваш JSON знаходиться у файлі user.json

const App = () => {
  return (
    <div>
      <Profile
        name={userData.name} // Передаємо нікнейм (ім'я) як пропс
        tag={userData.tag}
        location={userData.location}
        avatar={userData.avatar}
        stats={userData.stats}
      />
    </div>
  );
};

export default App;
