import React from 'react';
import profileStyles from './profile.styled';

const Profile = ({ name, tag, location, avatar, stats }) => {
  const { profile, description, avatar: avatarStyle, name: nameStyle, tag: tagStyle, location: locationStyle, stats: statsStyle, statItem, label, quantity } = profileStyles;

  return (
    <div style={profile}>
      <div className={description}>
        <img src={avatar} alt="User Avatar" style={avatarStyle} />
        <p style={nameStyle}>{name}</p>
        <p style={tagStyle}>@{tag}</p>
        <p style={locationStyle}>{location}</p>
      </div>
      <ul style={statsStyle}>
        <li style={statItem}>
          <span style={label}>Followers</span>
          <span style={quantity}>{stats.followers}</span>
        </li>
        <li style={statItem}>
          <span style={label}>Views</span>
          <span style={quantity}>{stats.views}</span>
        </li>
        <li style={statItem}>
          <span style={label}>Likes</span>
          <span style={quantity}>{stats.likes}</span>
        </li>
      </ul>
    </div>
  );
};

export default Profile;
