const statisticsStyles = {
  statistics: {
    backgroundColor: '#f0f0f0',
    padding: '20px',
    borderRadius: '10px',
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  statList: {
    listStyle: 'none',
    padding: '0',
  },
  item: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '10px',
  },
  label: {
    flex: 1,
    fontSize: '16px',
  },
  percentage: {
    fontSize: '16px',
    fontWeight: 'bold',
  },
};

export default statisticsStyles;
