import styled from 'styled-components';

export const FriendListWrapper = styled.ul`
  list-style: none;
  padding: 0;
`;

export const FriendListItemWrapper = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

export const Status = styled.span`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${({ isOnline }) => (isOnline ? 'green' : 'red')};
  margin-right: 10px;
`;

export const Avatar = styled.img`
  border-radius: 50%;
`;

export const Name = styled.p`
  font-size: 18px;
  font-weight: bold;
`;
