import React from 'react';
import PropTypes from 'prop-types';
import profileStyles from './profile.styled';

const Profile = ({ name, tag, location, avatar, stats }) => {
  return (
    <div style={profileStyles.profile}>
      <div style={profileStyles.description}>
        <img src={avatar} alt="User avatar" style={profileStyles.avatar} />
        <p style={profileStyles.name}>{name}</p>
        <p style={profileStyles.tag}>{tag}</p>
        <p style={profileStyles.location}>{location}</p>
      </div>

      <ul style={profileStyles.stats}>
        <li style={profileStyles.statItem}>
          <span style={profileStyles.label}>Followers</span>
          <span style={profileStyles.quantity}>{stats.followers}</span>
        </li>
        <li style={profileStyles.statItem}>
          <span style={profileStyles.label}>Views</span>
          <span style={profileStyles.quantity}>{stats.views}</span>
        </li>
        <li style={profileStyles.statItem}>
          <span style={profileStyles.label}>Likes</span>
          <span style={profileStyles.quantity}>{stats.likes}</span>
        </li>
      </ul>
    </div>
  );
};

Profile.propTypes = {
  name: PropTypes.string.isRequired,
  tag: PropTypes.string.isRequired,
  location: PropTypes.string.isRequired,
  avatar: PropTypes.string.isRequired,
  stats: PropTypes.shape({
    followers: PropTypes.number.isRequired,
    views: PropTypes.number.isRequired,
    likes: PropTypes.number.isRequired,
  }).isRequired,
};

export default Profile;
